mapper.select:
SELECT * FROM activity



            AND id = #{activityEntity.id}


            AND name = #{activityEntity.name}


            AND description = #{activityEntity.description}


            AND start_time = #{activityEntity.startTime}


            AND end_time = #{activityEntity.endTime}


            AND location = #{activityEntity.location}


            AND create_time = #{activityEntity.createTime}


            AND update_time = #{activityEntity.updateTime}


            AND status = #{activityEntity.status}
